import React from 'react';
import { Switch, Route } from 'react-router-dom'
import './App.css';
import HomePage from './pages/homepage/homepage.component'
import ShopPage from './pages/shop/shop.component'
import Header from './components/header/header.componenet'
import SignInAndSignUp from './pages/sign-in-and-sign-up/sign-in-and-sign-up.component'
import {auth, createUserProfileDocument} from './firebase/firebase.utils'
class App extends React.Component {
constructor(){
  super()
  this.state= {
    currentUser:null
  }
}
unsubscribeFromAuth=null;
componentDidMount(){
  //this.unsSubscribeFromAuth = firebase.auth().onAuthStateChanged() , this function
  // fires off whenever someone logs in or out and it takes in an async 
  //function with userAuth as a parameter, this parameter holds the value of the user information generated
  //by firebase (if the user is logged in) or null, if the user is logged out. 

  this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth =>{
    //if the user in fact logged in
    if (userAuth){
      //create a userRef variable, which awaits a return value from createUserProfileDocument
      // the return value is the document in firebase at /user/:userAuth.uid, if one doesnt exist, we create one and
      //return it
      const userRef = await createUserProfileDocument(userAuth);
      userRef.onSnapshot(snapShot=>{
        this.setState({
          id:snapShot.id,
          ...snapShot.data()

        }, ()=>console.log(this.state))
      })
    }
    this.setState({currentUser:userAuth});

  })
}
componentWillUnmount(){
  this.unsubscribeFromAuth();
}

  render() {
    return (
      <div>
        <Header currentUser = {this.state.currentUser}/>
        <Switch>
          <Route exact path='/' component={HomePage} />
          <Route path='/shop' component={ShopPage} />
          <Route path='/sign-in' component={SignInAndSignUp} />

        </Switch>
      </div>
    );
  }
}

export default App;
